import { Component } from "react";

class CommentList extends Component {
  state = {
    comments: [],
    isLoading: false,
  };
  getList = (props) => {
    this.setState({ isLoading: true });
    fetch("https://striveschool-api.herokuapp.com/api/comments/" + this.props.idSingleBook)
      .then((response) => {
        if (response.ok) {
          console.log("fetch conclusa");
          return response.json();
        } else {
          throw new Error("Errore nella richiesta delle prenotazioni al server");
        }
      })
      .then((films) => {
        this.setState({ films: films.Search });
      })
      .catch((err) => {
        console.log(err);
      })
      .finally(() => this.setState({ isLoading: false }));
  };

  componentDidMount() {
    this.CommentArea();
  }

  render() {
    return (
      <div className="bg-dark pt-3 pb-3">
        <h3 className="text-light px-4"> {this.props.galleryTitle}</h3>

        {this.state.isLoading && <div className="text-center loaderContainer"></div>}
      </div>
    );
  }
}
export default CommentList;
